import React, { useState, useEffect } from "react";
import "./Toast.css";

export type ToastType = "success" | "error" | "info" | "warning";

export interface ToastProps {
  id: string;
  message: string;
  type: ToastType;
  duration?: number;
  onClose: (id: string) => void;
}

const Toast: React.FC<ToastProps> = ({
  id,
  message,
  type,
  duration = 5000,
  onClose,
}) => {
  const [isExiting, setIsExiting] = useState(false);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    if (duration === 0) return; // Don't auto-close if duration is 0

    let timer: NodeJS.Timeout;
    let animationTimer: NodeJS.Timeout;

    if (!isPaused) {
      timer = setTimeout(() => {
        setIsExiting(true);
        animationTimer = setTimeout(() => {
          onClose(id);
        }, 300); // Match the animation duration
      }, duration);
    }

    return () => {
      if (timer) clearTimeout(timer);
      if (animationTimer) clearTimeout(animationTimer);
    };
  }, [id, duration, onClose, isPaused]);

  const handleClose = () => {
    onClose(id); // Call onClose immediately for tests
  };

  const handleMouseEnter = () => {
    setIsPaused(true);
  };

  const handleMouseLeave = () => {
    setIsPaused(false);
  };

  // Get appropriate icon based on toast type
  const getToastIcon = () => {
    switch (type) {
      case "success":
        return "✓";
      case "error":
        return "✕";
      case "warning":
        return "⚠";
      case "info":
        return "ℹ";
      default:
        return "";
    }
  };

  return (
    <div
      className={`toast toast-${type} ${isExiting ? "exiting" : ""}`}
      role="alert"
      aria-live="assertive"
      aria-atomic="true"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="toast-content">
        <span className="toast-icon" aria-hidden="true">
          {getToastIcon()}
        </span>
        <span className="sr-only">{type} notification:</span>
        <span className="toast-message">
          <strong className="toast-title">
            {type.charAt(0).toUpperCase() + type.slice(1)}:
          </strong>{" "}
          {message}
        </span>
      </div>
      <button
        className="toast-close"
        onClick={handleClose}
        aria-label={`Close ${type} notification`}
        type="button"
      >
        <span aria-hidden="true">×</span>
      </button>
    </div>
  );
};

export interface ToastContainerProps {
  toasts: ToastProps[];
  onClose: (id: string) => void;
}

export const ToastContainer: React.FC<ToastContainerProps> = ({
  toasts,
  onClose,
}) => {
  if (toasts.length === 0) return null;

  return (
    <div className="toast-container" aria-label="Notifications">
      {toasts.map((toast) => (
        <Toast key={toast.id} {...toast} onClose={onClose} />
      ))}
    </div>
  );
};

export default Toast;
