import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAsset, useDeleteAsset } from "../hooks/useAssets";
import AssetDetail from "../components/AssetDetail";
import ConfirmDialog from "../components/ConfirmDialog";

const AssetDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);

  // Convert id to number, handle invalid id
  const assetId = id ? parseInt(id, 10) : 0;
  const isValidId = !isNaN(assetId) && assetId > 0;

  const { asset, loading, error, refetch } = useAsset(isValidId ? assetId : 0);
  const {
    deleteAsset,
    loading: deleteLoading,
    error: deleteError,
  } = useDeleteAsset();

  // Handle invalid ID
  if (!isValidId) {
    return (
      <div
        style={{
          maxWidth: "1200px",
          margin: "0 auto",
          padding: "40px 20px",
          textAlign: "center",
        }}
      >
        <h1 style={{ color: "#dc3545", marginBottom: "16px" }}>
          Invalid Asset ID
        </h1>
        <p style={{ marginBottom: "24px", color: "#6c757d" }}>
          The asset ID provided is not valid.
        </p>
        <button
          onClick={() => navigate("/assets")}
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
            fontSize: "14px",
          }}
        >
          Back to Assets
        </button>
      </div>
    );
  }

  // Handle loading state
  if (loading) {
    return (
      <div
        style={{
          maxWidth: "1200px",
          margin: "0 auto",
          padding: "40px 20px",
          textAlign: "center",
        }}
      >
        <div
          style={{
            display: "inline-block",
            width: "40px",
            height: "40px",
            border: "4px solid #f3f3f3",
            borderTop: "4px solid #007bff",
            borderRadius: "50%",
            animation: "spin 1s linear infinite",
          }}
        />
        <p style={{ marginTop: "16px", color: "#6c757d" }}>Loading asset...</p>
        <style>
          {`
            @keyframes spin {
              0% { transform: rotate(0deg); }
              100% { transform: rotate(360deg); }
            }
          `}
        </style>
      </div>
    );
  }

  // Handle error state (including 404)
  if (error || !asset) {
    const isNotFound =
      error?.includes("404") || error?.includes("not found") || !asset;

    return (
      <div
        style={{
          maxWidth: "1200px",
          margin: "0 auto",
          padding: "40px 20px",
          textAlign: "center",
        }}
      >
        <h1
          style={{
            color: "#dc3545",
            marginBottom: "16px",
          }}
        >
          {isNotFound ? "Asset Not Found" : "Error Loading Asset"}
        </h1>
        <p style={{ marginBottom: "24px", color: "#6c757d" }}>
          {isNotFound
            ? `The asset with ID ${assetId} could not be found. It may have been deleted or the ID may be incorrect.`
            : error || "An unexpected error occurred while loading the asset."}
        </p>
        <div style={{ display: "flex", gap: "12px", justifyContent: "center" }}>
          <button
            onClick={() => navigate("/assets")}
            style={{
              padding: "10px 20px",
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
              fontSize: "14px",
            }}
          >
            Back to Assets
          </button>
          {!isNotFound && (
            <button
              onClick={() => refetch()}
              style={{
                padding: "10px 20px",
                backgroundColor: "#6c757d",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "14px",
              }}
            >
              Try Again
            </button>
          )}
        </div>
      </div>
    );
  }

  // Handle edit action
  const handleEdit = () => {
    navigate(`/assets/${assetId}/edit`);
  };

  // Handle delete action
  const handleDelete = () => {
    setShowDeleteDialog(true);
  };

  // Handle delete confirmation
  const handleConfirmDelete = async () => {
    const success = await deleteAsset(assetId);
    if (success) {
      navigate("/assets", {
        state: {
          message: `Asset "${asset.name}" has been successfully deleted.`,
          type: "success",
        },
      });
    } else {
      setShowDeleteDialog(false);
      // Error is handled by the hook and will be displayed
    }
  };

  // Handle delete cancellation
  const handleCancelDelete = () => {
    setShowDeleteDialog(false);
  };

  return (
    <>
      <AssetDetail asset={asset} onEdit={handleEdit} onDelete={handleDelete} />

      <ConfirmDialog
        isOpen={showDeleteDialog}
        title="Delete Asset"
        message={`Are you sure you want to delete "${asset.name}"? This action cannot be undone.`}
        confirmText={deleteLoading ? "Deleting..." : "Delete"}
        cancelText="Cancel"
        onConfirm={handleConfirmDelete}
        onCancel={handleCancelDelete}
        isDestructive={true}
      />

      {deleteError && (
        <div
          style={{
            position: "fixed",
            top: "20px",
            right: "20px",
            backgroundColor: "#f8d7da",
            color: "#721c24",
            padding: "12px 16px",
            borderRadius: "6px",
            border: "1px solid #f5c6cb",
            maxWidth: "400px",
            zIndex: 1000,
          }}
        >
          <strong>Delete Failed:</strong> {deleteError}
        </div>
      )}
    </>
  );
};

export default AssetDetailPage;
